import React, { useState } from "react";

const BooksComponent = () => {
  const [cart, setCart] = useState([]);

  // Sample books data
  const books = [
    { id: 1, title: "JavaScript Mastery", price: 499 },
    { id: 2, title: "React Basics", price: 399 },
    { id: 3, title: "Node.js Guide", price: 599 },
  ];

  const addToCart = (book) => {
    if (!cart.find((item) => item.id === book.id)) {
      setCart([...cart, book]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  const purchase = () => {
    alert("Purchase successful!");
    setCart([]);
  };

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <h1>ðŸ“š Book Store</h1>

      {/* Books Listing */}
      <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
        {books.map((book) => (
          <div
            key={book.id}
            style={{
              border: "1px solid #ccc",
              borderRadius: "10px",
              padding: "15px",
              width: "200px",
              textAlign: "center",
            }}
          >
            <h3>{book.title}</h3>
            <p>â‚¹{book.price}</p>
            <button
              onClick={() => addToCart(book)}
              style={{
                background: "blue",
                color: "white",
                padding: "5px 10px",
                border: "none",
                borderRadius: "5px",
              }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {/* Cart Section */}
      <h2 style={{ marginTop: "30px" }}>ðŸ›’ Your Cart</h2>
      {cart.length === 0 ? (
        <p>Cart is empty</p>
      ) : (
        <div>
          {cart.map((item) => (
            <div
              key={item.id}
              style={{
                display: "flex",
                justifyContent: "space-between",
                marginBottom: "10px",
                background: "#f9f9f9",
                padding: "10px",
                borderRadius: "5px",
              }}
            >
              <span>
                {item.title} - â‚¹{item.price}
              </span>
              <button
                onClick={() => removeFromCart(item.id)}
                style={{
                  background: "red",
                  color: "white",
                  padding: "3px 8px",
                  border: "none",
                  borderRadius: "5px",
                }}
              >
                Remove
              </button>
            </div>
          ))}

          <button
            onClick={purchase}
            style={{
              marginTop: "10px",
              background: "green",
              color: "white",
              padding: "8px 15px",
              border: "none",
              borderRadius: "5px",
            }}
          >
            Purchase
          </button>
        </div>
      )}
    </div>
  );
};

export default BooksComponent;
