import React, { useState } from "react";

export default function AuthForm() {
  const [mode, setMode] = useState("userLogin"); // userLogin, adminLogin, signup

  return (
    <div
      className="flex justify-center items-center min-h-screen 
                 bg-[url('https://images.unsplash.com/photo-1512820790803-83ca734da794')] 
                 bg-cover bg-center"
    >
      <div className="w-full max-w-md bg-white bg-opacity-90 p-6 rounded-lg shadow-lg">
        <h1 className="text-2xl font-bold text-center mb-6">
          {mode === "userLogin" && "User Login"}
          {mode === "adminLogin" && "Admin Login"}
          {mode === "signup" && "Signup"}
        </h1>

        {/* Form */}
        <form className="space-y-4">
          {mode === "signup" && (
            <input
              type="text"
              placeholder="Name"
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring focus:border-blue-300"
            />
          )}
          <input
            type="email"
            placeholder="Email"
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring focus:border-blue-300"
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring focus:border-blue-300"
          />

          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition"
          >
            {mode === "signup" ? "Signup" : "Login"}
          </button>
        </form>

        {/* Switch Links */}
        <div className="mt-6 text-center space-y-2">
          {mode !== "userLogin" && (
            <button
              onClick={() => setMode("userLogin")}
              className="text-blue-500 hover:underline block"
            >
              User Login
            </button>
          )}
          {mode !== "adminLogin" && (
            <button
              onClick={() => setMode("adminLogin")}
              className="text-blue-500 hover:underline block"
            >
              Admin Login
            </button>
          )}
          {mode !== "signup" && (
            <button
              onClick={() => setMode("signup")}
              className="text-blue-500 hover:underline block"
            >
              Signup
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
