import axios from "axios";
import React, { useState, useEffect } from "react";

const API_URL = "http://localhost:4000/api/books";

const BooksManager = ({ role }) => {
  const [books, setBooks] = useState([]);
  const [issuedBooks, setIssuedBooks] = useState([]);
  const [cart, setCart] = useState([]);

  // Form state for admin add book
  const [newTitle, setNewTitle] = useState("");
  const [newAuthor, setNewAuthor] = useState("");

  const MAX_BOOKS_LIMIT = 5;
  const userCanTakeMoreBooks = cart.length < MAX_BOOKS_LIMIT;

  // Fetch all books
  useEffect(() => {
    axios.get(API_URL).then((res) => setBooks(res.data));
  }, []);

  // Add book (Admin only)
  const addBook = () => {
    if (!newTitle || !newAuthor) {
      alert("Please enter both title and author");
      return;
    }
    axios.post(API_URL, { title: newTitle, author: newAuthor }).then((res) => {
      setBooks([...books, res.data]);
      setNewTitle("");
      setNewAuthor("");
    });
  };

  // Delete book (Admin only)
  const deleteBook = (id) => {
    axios.delete(`${API_URL}/${id}`).then(() => {
      setBooks(books.filter((book) => book._id !== id));
    });
  };

  // Add to cart with limit check
  const addToCart = (book) => {
    if (!userCanTakeMoreBooks) {
      alert("आपने 5 books की limit पूरी कर ली है। और books add नहीं कर सकते।");
      return;
    }
    if (!cart.find((item) => item._id === book._id)) {
      setCart([...cart, book]);
    }
  };

  // Issue books (User only)
  const issueBooks = () => {
    if (cart.length === 0) {
      alert("No books in cart");
      return;
    }
    setIssuedBooks([...issuedBooks, ...cart]);
    setCart([]);
    alert("Books issued successfully");
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Books Manager</h1>

      {/* Admin Add Book Section */}
      {role === "admin" && (
        <div className="mb-4 space-y-2">
          <input
            type="text"
            placeholder="Book Title"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
            className="border p-2 rounded w-full"
          />
          <input
            type="text"
            placeholder="Author"
            value={newAuthor}
            onChange={(e) => setNewAuthor(e.target.value)}
            className="border p-2 rounded w-full"
          />
          <button
            onClick={addBook}
            className="bg-green-600 text-white px-4 py-2 rounded"
          >
            Add Book
          </button>
        </div>
      )}

      {/* Books List */}
      <div className="grid grid-cols-3 gap-4">
        {books.map((book) => (
          <div key={book._id} className="border p-4 rounded">
            <h2 className="text-lg font-semibold">{book.title}</h2>
            <p className="text-gray-600">{book.author}</p>

            {role === "admin" ? (
              <button
                onClick={() => deleteBook(book._id)}
                className="mt-2 bg-red-600 text-white px-3 py-1 rounded"
              >
                Delete
              </button>
            ) : (
              <button
                onClick={() => {
                  if (!userCanTakeMoreBooks) {
                    alert(
                      "आपने 5 books की limit पूरी कर ली है। और books add नहीं कर सकते।"
                    );
                  } else {
                    addToCart(book);
                  }
                }}
                disabled={
                  !userCanTakeMoreBooks || cart.find((c) => c._id === book._id)
                }
                className={`mt-2 px-3 py-1 rounded cursor-pointer ${
                  cart.find((c) => c._id === book._id)
                    ? "bg-gray-500 text-white"
                    : "bg-blue-600 text-white"
                }`}
              >
                {cart.find((c) => c._id === book._id) ? "Added" : "Add to Cart"}
              </button>
            )}
          </div>
        ))}
      </div>

      {/* Cart Section */}
      {role === "user" && (
        <div className="mt-6">
          <h2 className="text-xl font-bold mb-2">Cart ({cart.length})</h2>
          <ul className="list-disc pl-6">
            {cart.map((book) => (
              <li key={book._id}>
                {book.title} - {book.author}
              </li>
            ))}
          </ul>
          <button
            onClick={issueBooks}
            className="mt-4 bg-purple-600 text-white px-4 py-2 rounded"
          >
            Issue Books
          </button>
        </div>
      )}

      {/* Issued Books Section */}
      {role === "user" && issuedBooks.length > 0 && (
        <div className="mt-6">
          <h2 className="text-xl font-bold mb-2">Issued Books</h2>
          <ul className="list-disc pl-6">
            {issuedBooks.map((book) => (
              <li key={book._id}>
                {book.title} - {book.author}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default BooksManager;
