//frontend/new-app/src/components/BooksManager.js
import axios from "axios";
import React, { useState, useEffect } from "react";

const API_URL = "http://localhost:4000/api/books";

const BooksManager = ({ role }) => {
  const [books, setBooks] = useState([]);
  const [issuedBooks, setIssuedBooks] = useState([]);
  const [cart, setCart] = useState([]);

  // Form state for admin add book
  const [newTitle, setNewTitle] = useState("");
  const [newAuthor, setNewAuthor] = useState("");
  const [newImage, setNewImage] = useState("");

  // Show issued books toggle for user/admin
  const [showIssuedBooks, setShowIssuedBooks] = useState(false);
  const [showAdminIssuedBooks, setShowAdminIssuedBooks] = useState(false);

  const MAX_BOOKS_ALLOWED = 5;

  // Fetch books and issuedBooks on component mount
  useEffect(() => {
    fetchBooks();
    fetchIssuedBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const res = await axios.get(API_URL);
      setBooks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchIssuedBooks = async () => {
    try {
      const res = await axios.get(`${API_URL}/issued`);
      setIssuedBooks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Add book (Admin)
  const addBook = async (e) => {
    e.preventDefault();
    if (!newTitle || !newAuthor || !newImage) {
      alert("Please fill all fields (Title, Author, Image URL)");
      return;
    }
    try {
      await axios.post(API_URL, {
        title: newTitle,
        author: newAuthor,
        image: newImage,
      });
      setNewTitle("");
      setNewAuthor("");
      setNewImage("");
      fetchBooks();
    } catch (err) {
      alert("Error adding book");
      console.error(err);
    }
  };

  // Add book to cart (User)
  const totalUserBooks = issuedBooks.length + cart.length;
  const userCanTakeMoreBooks = totalUserBooks < MAX_BOOKS_ALLOWED;
  const booksRemainingForUser = MAX_BOOKS_ALLOWED - totalUserBooks;

  const addToCart = (book) => {
    if (totalUserBooks >= MAX_BOOKS_ALLOWED) {
      alert("You have already reached maximum limit of 5 books.");
      return;
    }
    if (!cart.find((item) => item._id === book._id)) {
      setCart([...cart, book]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item._id !== id));
  };

  // Issue books
  const issueBooks = async () => {
    if (cart.length === 0) {
      alert("Cart is empty");
      return;
    }
    const issueDate = new Date();
    const returnDate = new Date();
    returnDate.setDate(issueDate.getDate() + 7);

    const newIssued = cart.map((book) => ({
      bookId: book._id,
      title: book.title,
      issueDate: issueDate.toISOString().split("T")[0],
      returnDate: returnDate.toISOString().split("T")[0],
    }));

    try {
      await axios.post(`${API_URL}/issued`, { books: newIssued });
      setCart([]);
      fetchIssuedBooks();
      alert("Books issued successfully!");
    } catch (err) {
      alert("Error issuing books");
      console.error(err);
    }
  };

  // Calculate fine ‚Çπ5/day after return date
  const calculateFine = (returnDate) => {
    const today = new Date();
    const rDate = new Date(returnDate);
    const diffTime = today - rDate;
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
    return diffDays > 0 ? diffDays * 5 : 0;
  };

  // Handle return book (Admin)
  const handleReturnBook = async (issuedBookId) => {
    const book = issuedBooks.find((b) => b._id === issuedBookId);
    if (!book) return;

    const fine = calculateFine(book.returnDate);

    if (fine > 0) {
      const payFine = window.confirm(
        `This book has a fine of ‚Çπ${fine}. Please pay before returning.\n\nPay fine now?`
      );
      if (!payFine) {
        alert("Please pay the fine to return the book.");
        return;
      } else {
        alert(`‚Çπ${fine} fine paid successfully.`);
      }
    } else {
      alert("Book returned successfully.");
    }

    try {
      await axios.delete(`${API_URL}/issued/${issuedBookId}`);
      fetchIssuedBooks();
    } catch (err) {
      alert("Error returning book");
      console.error(err);
    }
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("role");
    alert("You have logged out.");
    window.location.reload();
  };

  // --- UI same ‡§ú‡•à‡§∏‡§æ ‡§Ü‡§™‡§®‡•á ‡§¶‡§ø‡§Ø‡§æ ‡§•‡§æ, ‡§¨‡§∏ id ‡§ï‡•Ä ‡§ú‡§ó‡§π _id ‡§á‡§∏‡•ç‡§§‡•á‡§Æ‡§æ‡§≤ ‡§ï‡§∞‡•á‡§Ç ---

  if (role === "admin") {
    return (
      <div className="max-w-5xl mx-auto p-6 font-sans">
        <h1 className="text-3xl font-bold mb-6">Admin Panel</h1>

        {!userCanTakeMoreBooks && (
          <div className="mb-4 p-4 bg-red-100 text-red-700 rounded border border-red-300">
            User already has 5 books issued/carted. Availability is limited.
          </div>
        )}

        {userCanTakeMoreBooks && (
          <div className="mb-4 p-4 bg-green-100 text-green-700 rounded border border-green-300">
            User can take {booksRemainingForUser} more book
            {booksRemainingForUser > 1 ? "s" : ""}.
          </div>
        )}

        {/* Add Book Form */}
        <div className="mb-8 p-6 bg-white shadow rounded-lg">
          <h2 className="text-2xl font-semibold mb-4">Add New Book</h2>
          <form className="flex flex-col gap-4" onSubmit={addBook}>
            <input
              type="text"
              placeholder="Book Title"
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
              className="border p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="text"
              placeholder="Author Name"
              value={newAuthor}
              onChange={(e) => setNewAuthor(e.target.value)}
              className="border p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="text"
              placeholder="Image URL"
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
              className="border p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-5 py-2 rounded-md hover:bg-blue-700 transition"
            >
              Add Book
            </button>
          </form>
        </div>

        {/* Toggle Issued Books */}
        <button
          onClick={() => setShowAdminIssuedBooks(!showAdminIssuedBooks)}
          className="mb-4 px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition"
        >
          {showAdminIssuedBooks ? "Hide Issued Books" : "Show Issued Books"}
        </button>

        {showAdminIssuedBooks && (
          <div className="bg-white shadow rounded-lg p-6">
            <h2 className="text-2xl font-semibold mb-4">Issued Books</h2>
            {issuedBooks.length === 0 ? (
              <p>No issued books.</p>
            ) : (
              <table className="min-w-full table-auto border-collapse border border-gray-300">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Book Title
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Issue Date
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Return Date
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Fine (‚Çπ5/day)
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {issuedBooks.map((book) => {
                    const fine = calculateFine(book.returnDate);
                    return (
                      <tr key={book._id} className="hover:bg-gray-50">
                        <td className="border border-gray-300 px-4 py-2">
                          {book.title}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          {book.issueDate}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          {book.returnDate}
                        </td>
                        <td
                          className={`border border-gray-300 px-4 py-2 ${
                            fine > 0
                              ? "text-red-600 font-bold"
                              : "text-green-600"
                          }`}
                        >
                          ‚Çπ{fine}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          <button
                            onClick={() => handleReturnBook(book._id)}
                            className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded-md transition"
                          >
                            Return Book
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            )}
          </div>
        )}

        <button
          onClick={handleLogout}
          className="mt-6 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
        >
          Logout
        </button>
      </div>
    );
  }

  // User UI
  return (
    <div className="max-w-5xl mx-auto p-6 font-sans">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">üìö Book Store</h1>
        <div>
          <button
            onClick={() => setShowIssuedBooks(false)}
            className={`mr-4 px-4 py-2 rounded-md transition ${
              !showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            Books
          </button>
          <button
            onClick={() => setShowIssuedBooks(true)}
            className={`px-4 py-2 rounded-md transition ${
              showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            My Issued Books
          </button>
          <button
            onClick={handleLogout}
            className="ml-4 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
          >
            Logout
          </button>
        </div>
      </div>

      {!showIssuedBooks && (
        <>
          {/* Books Listing */}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {books.length === 0 ? (
              <p className="text-gray-600">No books available currently.</p>
            ) : (
              books.map((book) => (
                <div
                  key={book._id}
                  className="bg-white shadow rounded-lg overflow-hidden flex flex-col"
                >
                  <img
                    src={book.image}
                    alt={book.title}
                    className="h-48 w-full object-cover"
                  />
                  <div className="p-4 flex-grow flex flex-col justify-between">
                    <div>
                      <h2 className="font-semibold text-lg">{book.title}</h2>
                      <p className="text-gray-600 text-sm mb-2">
                        {book.author}
                      </p>
                    </div>
                    <button
                      onClick={() => addToCart(book)}
                      disabled={
                        !userCanTakeMoreBooks ||
                        cart.find((c) => c._id === book._id)
                      }
                      className={`mt-2 px-3 py-1 rounded-md text-white ${
                        userCanTakeMoreBooks &&
                        !cart.find((c) => c._id === book._id)
                          ? "bg-blue-600 hover:bg-blue-700"
                          : "bg-gray-400 cursor-not-allowed"
                      }`}
                    >
                      {cart.find((c) => c._id === book._id)
                        ? "Added"
                        : "Add to Cart"}
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>

          {/* Cart */}
          <div className="mt-8">
            <h2 className="text-xl font-semibold mb-2">Cart</h2>
            {cart.length === 0 ? (
              <p>No books in cart.</p>
            ) : (
              <ul>
                {cart.map((book) => (
                  <li
                    key={book._id}
                    className="flex justify-between items-center border p-2 rounded mb-2"
                  >
                    {book.title}
                    <button
                      onClick={() => removeFromCart(book._id)}
                      className="text-red-600 hover:underline"
                    >
                      Remove
                    </button>
                  </li>
                ))}
              </ul>
            )}
            <button
              onClick={issueBooks}
              disabled={cart.length === 0}
              className={`mt-4 px-4 py-2 rounded-md text-white ${
                cart.length > 0
                  ? "bg-green-600 hover:bg-green-700"
                  : "bg-gray-400 cursor-not-allowed"
              }`}
            >
              Issue Books
            </button>
          </div>
        </>
      )}

      {showIssuedBooks && (
        <div>
          <h2 className="text-xl font-semibold mb-4">My Issued Books</h2>
          {issuedBooks.length === 0 ? (
            <p>No books issued currently.</p>
          ) : (
            <table className="min-w-full table-auto border-collapse border border-gray-300">
              <thead>
                <tr className="bg-gray-100">
                  <th className="border border-gray-300 px-4 py-2 text-left">
                    Book Title
                  </th>
                  <th className="border border-gray-300 px-4 py-2 text-left">
                    Issue Date
                  </th>
                  <th className="border border-gray-300 px-4 py-2 text-left">
                    Return Date
                  </th>
                  <th className="border border-gray-300 px-4 py-2 text-left">
                    Fine (‚Çπ5/day)
                  </th>
                </tr>
              </thead>
              <tbody>
                {issuedBooks.map((book) => {
                  const fine = calculateFine(book.returnDate);
                  return (
                    <tr key={book._id} className="hover:bg-gray-50">
                      <td className="border border-gray-300 px-4 py-2">
                        {book.title}
                      </td>
                      <td className="border border-gray-300 px-4 py-2">
                        {book.issueDate}
                      </td>
                      <td className="border border-gray-300 px-4 py-2">
                        {book.returnDate}
                      </td>
                      <td
                        className={`border border-gray-300 px-4 py-2 ${
                          fine > 0 ? "text-red-600 font-bold" : "text-green-600"
                        }`}
                      >
                        ‚Çπ{fine}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
};

export default BooksManager;
