import { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [form, setForm] = useState({ title: "", desc: "" });
  const [todo, setTodo] = useState([]);
  const[dId,setdId]=uses(false)
  const[edId,setedId]=useState(null)


  const api_url = "http://localhost:4000/todos";

  // GET todos
  useEffect(() => {
    const getTodo = async () => {
      try {
        const res = await axios.get(api_url);
        console.log("Fetched Todos:", res.data); // ✅ Check data in console
        setTodo(res.data);
      } catch (err) {
        console.log("Fetching error", err);
      }
    };
    getTodo();
  }, []);

  // Handle Input Change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Submit Form
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (edId) {
        await axios.put(`${API_URL}/${edId}`, form);
        setedId(false);
        setdId(null);
      }else{
      const res = await axios.post(api_url, form);
      setTodo([...todo, res.data]);

      }
      setForm({ title: "", desc: "" }); 

    } catch (err) {
      console.log("Submit error", err);
    }
  };

  // Delete Todo
  const deleteId = async (id) => {
    try {
      await axios.delete(`${api_url}/${id}`);
      setTodo(todo.filter((item) => item._id !== id));
    } catch (err) {
      console.log("Delete error", err);
    }
  };

  //Edited
const editId = async (e_id) => {

      const ed=todo.find((item)=>item._id==e_id)
      setTodo({title:ed.title,desc:ed.desc});
setdId(true)
setedId(e_id)

  };


  return (
    <div style={{ textAlign: "center" }}>
      <form onSubmit={handleSubmit}>
        <h1>Todo App</h1>

        Title:
        <input
          type="text"
          name="title"
          placeholder="Enter title"
          value={form.title}
          onChange={handleChange}
        />
        <br />

        Description:
        <input
          type="text"
          name="desc"
          placeholder="Enter description"
          value={form.desc}
          onChange={handleChange}
        />
        <br />

        <button type="submit">Save</button>
      </form>

      <hr />

      {todo.length > 0 ? (
        todo.map((item) => (
          <div key={item._id} style={{ border: "1px solid gray", margin: "10px", padding: "10px" }}>
            <h3>{item.title}</h3>
            <p>{item.desc}</p>
            <button onClick={()=>editId(item._id)}>Edit</button>
            <button onClick={() => deleteId(item._id)}>Delete</button>
          </div>
        ))
      ) : (
        <p>No todos yet...</p>
      )}
    </div>
  );
};

export default App;
