import { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [form, setForm] = useState({ title: "", desc: "" });
  const [todo, setTodo] = useState([]);
  const api_url = "http://localhost:4000/todos";

  // Fetching all todos when page loads
  useEffect(() => {
    const getTodo = async () => {
      try {
        let res = await axios.get(api_url);
        setTodo(res.data);
      } catch (err) {
        console.log("fetching error", err);
      }
    };
    getTodo();
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let res = await axios.post(api_url, form);
      setTodo([...todo, res.data]); // ✅ use response with _id
      setForm({ title: "", desc: "" }); // ✅ clear form
    } catch (err) {
      console.log("submit error", err);
    }
  };

  // Handle delete
  const deleteId = async (id) => {
    try {
      await axios.delete(`${api_url}/${id}`);
      setTodo(todo.filter((item) => item._id !== id));
    } catch (err) {
      console.log("error", err);
    }
  };

  return (
    <div style={{ textAlign: "center" }}>
      <form onSubmit={handleSubmit}>
        <h1>Todo App</h1>
        Title:
        <input
          type="text"
          placeholder="Enter The Title"
          name="title"
          value={form.title}
          onChange={handleChange}
        />
        <br />
        Description:
        <input
          type="text"
          placeholder="Enter The Description"
          name="desc"
          value={form.desc}
          onChange={handleChange}
        />
        <br />
        <button type="submit">Save</button>
        <br />
      </form>

      {todo.length > 0 ? (
        todo.map((item) => (
          <div key={item._id} style={{ border: "1px solid gray", margin: "10px", padding: "10px" }}>
            <h3>{item.title}</h3>
            <p>{item.desc}</p>
            <button type="button">Edit</button>
            <button type="button" onClick={() => deleteId(item._id)}>
              Delete
            </button>
          </div>
        ))
      ) : (
        "No todo yet..."
      )}
    </div>
  );
};

export default App;
