import react, { useState } from "react"

const App = () => {

  const[form ,setForm]=useState({title:"",desc:""})
  const[todo,setTodo]=useState([])


let  handleSubmit=(e)=>{
 e.preventDefault()

  }

  let  handleChange=(e)=>{
setForm({...form,[e.target.name]:e.target.value})

  }

  return (
    <div style={{textAlign:'center'}}>
      <form onSubmit={handleSubmit}>
      <h1>Do-to App</h1>

      Title:<input type="text" placeholder="Enter The Title" name="title" value={form.value} onClick={handleChange}/><br></br>

      description:<input type="text" placeholder="Enter The Description" onClick={handleChange} value={form.value}/><br/>

      <button type="submit" >Save</button><br/>


      </form>

      {todo.length>0?(
        todo.map((item)=>{
          <div>
          <h1>To do Details</h1>
           <h3>{item.title}</h3>
           <p3>{item.desc}</p3>
          </div>
        })
      )

      :('No todo yet...')}

    </div>


  )


}

export default App
