  return (
  <div style={{ textAlign: 'center' }}>
    <form onSubmit={handleSubmit}>
      Name: <input type="text" placeholder="Enter The Name" onChange={handleChange} name="name" value={form.name} /><br />
      Father Name: <input type="text" placeholder="Enter The Father Name" onChange={handleChange} name="fatherName" value={form.fatherName} /><br />
      Mobile Number: <input type="text" placeholder="Enter The Mobile Number" onChange={handleChange} name="mobileNumber" value={form.mobileNumber} /><br />
      <button type="submit">Submit</button>
    </form>

    <h1>Display Detail</h1>
    <hr />
    <center>
      <table border="1" width="600px" cellSpacing="0">
        <thead>
          <tr>
            <th>Name</th>
            <th>Father Name</th>
            <th>Mobile Number</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{item.name}</td>
              <td>{item.fatherName}</td>
              <td>{item.mobileNumber}</td>
              <td><button type="button" onClick={() => handleDelete(item._id)}>Delete</button>
             <button type="button" onClick={() => handleEdit(item._id)} style={{gap:'2px'}}>Edit</button ></td>
            </tr>
          ))}
        </tbody>
      </table>
    </center>
  </div>
);
}

export default App
