import React, { useState } from "react";

const BooksComponent = () => {
  const [cart, setCart] = useState([]);

  const books = [
    { id: 1, title: "JavaScript Mastery", price: 499 },
    { id: 2, title: "React Basics", price: 399 },
    { id: 3, title: "Node.js Guide", price: 599 },
    { id: 4, title: "CSS Essentials", price: 299 },  // Extra book for testing
  ];

  const addToCart = (book) => {
    if (cart.length >= 3) {
      alert("You can add maximum 3 books to the cart.");
      return;
    }
    if (!cart.find((item) => item.id === book.id)) {
      setCart([...cart, book]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  const purchase = () => {
    alert("Purchase successful!");
    setCart([]);
  };

  const handleLogout = () => {
    localStorage.removeItem("role");
    alert("You have logged out.");
    window.location.reload();
  };

  return (
    <div className="max-w-5xl mx-auto p-6 font-sans">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">ðŸ“š Book Store</h1>
        <button
          onClick={handleLogout}
          className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
        >
          Logout
        </button>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {books.map((book) => (
          <div
            key={book.id}
            className="border rounded-lg p-5 shadow hover:shadow-lg transition bg-white"
          >
            <h3 className="text-xl font-semibold mb-2 text-gray-900">{book.title}</h3>
            <p className="text-gray-700 mb-4">â‚¹{book.price}</p>
            <button
              onClick={() => addToCart(book)}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-md transition"
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      <h2 className="text-2xl font-semibold mt-12 mb-6 text-gray-800">ðŸ›’ Your Cart</h2>

      {cart.length === 0 ? (
        <p className="text-gray-600">Cart is empty</p>
      ) : (
        <div className="space-y-4">
          {cart.map((item) => (
            <div
              key={item.id}
              className="flex justify-between items-center bg-gray-100 p-4 rounded-md shadow"
            >
              <span className="text-gray-900 font-medium">
                {item.title} - â‚¹{item.price}
              </span>
              <button
                onClick={() => removeFromCart(item.id)}
                className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md transition"
              >
                Remove
              </button>
            </div>
          ))}

          <button
            onClick={purchase}
            className="mt-6 bg-green-600 hover:bg-green-700 text-white py-3 px-6 rounded-md font-semibold transition"
          >
            Purchase
          </button>
        </div>
      )}
    </div>
  );
};

export default BooksComponent;
