import React,{useState,useEffect} from 'react'
import axios from 'axios'

const App = () => {
const [form,setform]=useState({name:'',fatherName:'',mobileNumber:''})
const[data,setData]=useState([])

const API_URL='http://localhost:4000/'

const handleSubmit=(e)=>{
e.preventDefault()
setData([...data,form]);
axios.post(API_URL+'save',form)
}

useEffect(()=>{

const showData=async()=>{
const res=await axios.get(API_URL+'fetchData')
setData(res.data); 
console.log(res.data)
}
showData()
},[])



const handleChange=(e)=>{
  setform({...form,[e.target.name]:e.target.value})
}

const handleDelete=async(id)=>{
await axios.delete(`${API_URL}delete/${id}`)
setData(data.filter((item)=>item._id!==id)); 
}

  return (
    <div style={{textAlign:'center'}}>
  <form onSubmit={handleSubmit}>
    Name:<input type="text" placeholder='Enter The Name' onChange={handleChange} name="name" value={form.name}/><br/>
    Father Name:<input type="text" placeholder='Enter The Father Name'onChange={handleChange} name="fatherName"  value={form.fatherName}/><br/>
    Mobile Number:<input type="text" placeholder='Enter The Mobile Number'onChange={handleChange} name="mobileNumber"  value={form.mobileNumber}/><br/>
    <button type='submit'>submit</button>
  </form>
  
  <h1>Display Detail</h1>
<hr/>
<center>
 <table border='1'  width='300px' align='center'>
      <tr>
    <th>Name</th>
    <th>Father Name</th>
    <th>Mobile Number</th>
    <th>Action</th>
   </tr>
   </table>
   </center>
  {data.map((item)=>(
    <center>
  <div key={item._id} >
    <table border="1"  cellSpacing='0px'  width='300px' align='center'>
   
   <tr>
  <th>{item.name}</th>
  <th>{item.fatherName}</th>
  <th>{item.mobileNumber}</th>
  <button type="button" onClick={()=>handleDelete(item._id)}>Delete</button>
 </tr>
  
</table>
    </div>
    </center>
   )) }
    </div>
  )
}

export default App
