import axios from "axios";
import React, { useState, useEffect } from "react";

const API_URL = "http://localhost:4000/api/books";

const BooksManager = ({ role }) => {
  const [books, setBooks] = useState([]);
  const [issuedBooks, setIssuedBooks] = useState([]);
  const [cart, setCart] = useState([]);

  // Form state for admin add book
  const [newTitle, setNewTitle] = useState("");
  const [newAuthor, setNewAuthor] = useState("");
  const [newImage, setNewImage] = useState("");

  // Show issued books toggle for user/admin
  const [showIssuedBooks, setShowIssuedBooks] = useState(false);
  const [showAdminIssuedBooks, setShowAdminIssuedBooks] = useState(false);

  // Search states
  const [userSearchEmail, setUserSearchEmail] = useState(""); // admin -> search issued by email
  const [titleSearch, setTitleSearch] = useState(""); // user -> search books by title (client-side)

  // Edit book state (admin)
  const [editingBook, setEditingBook] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editAuthor, setEditAuthor] = useState("");
  const [editImage, setEditImage] = useState("");

  const MAX_BOOKS_ALLOWED = 5;

  // read logged-in user's email from localStorage
  const userEmail = localStorage.getItem("userEmail");

  // Fetch books and issuedBooks on component mount and when role/userEmail changes
  useEffect(() => {
    fetchBooks();
    fetchIssuedBooks();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [role, userEmail]);

  const fetchBooks = async () => {
    try {
      const res = await axios.get(API_URL);
      setBooks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchIssuedBooks = async (emailQuery = null) => {
    try {
      const url =
        role === "user"
          ? `${API_URL}/issued?userEmail=${encodeURIComponent(userEmail || "")}`
          : emailQuery
          ? `${API_URL}/issued?userEmail=${encodeURIComponent(emailQuery)}`
          : `${API_URL}/issued`;
      const res = await axios.get(url);
      setIssuedBooks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Add book (Admin)
  const addBook = async (e) => {
    e.preventDefault();
    if (!newTitle || !newAuthor || !newImage) {
      alert("Please fill all fields (Title, Author, Image URL)");
      return;
    }
    try {
      await axios.post(API_URL, {
        title: newTitle,
        author: newAuthor,
        image: newImage,
      });
      setNewTitle("");
      setNewAuthor("");
      setNewImage("");
      fetchBooks();
    } catch (err) {
      alert("Error adding book");
      console.error(err);
    }
  };

  // Update book (Admin)
  const startEdit = (book) => {
    setEditingBook(book);
    setEditTitle(book.title || "");
    setEditAuthor(book.author || "");
    setEditImage(book.image || "");
  };

  const cancelEdit = () => {
    setEditingBook(null);
    setEditTitle("");
    setEditAuthor("");
    setEditImage("");
  };

  const saveEdit = async () => {
    if (!editTitle || !editAuthor || !editImage) {
      alert("Fill all fields to update");
      return;
    }
    try {
      await axios.put(`${API_URL}/${editingBook._id}`, {
        title: editTitle,
        author: editAuthor,
        image: editImage,
      });
      cancelEdit();
      fetchBooks();
      alert("Book updated successfully");
    } catch (err) {
      alert("Error updating book");
      console.error(err);
    }
  };

  // Add book to cart (User)
  const totalUserBooks = issuedBooks.length + cart.length;
  const userCanTakeMoreBooks = totalUserBooks < MAX_BOOKS_ALLOWED;
  const booksRemainingForUser = MAX_BOOKS_ALLOWED - totalUserBooks;

  const addToCart = (book) => {
    if (totalUserBooks >= MAX_BOOKS_ALLOWED) {
      alert("You have already reached maximum limit of 5 books.");
      return;
    }
    if (!cart.find((item) => item._id === book._id)) {
      setCart([...cart, book]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item._id !== id));
  };

  // Issue books
  const issueBooks = async () => {
    if (cart.length === 0) {
      alert("Cart is empty");
      return;
    }
    if (!userEmail) {
      alert("User not identified. Please login again.");
      return;
    }

    const issueDate = new Date();
    const returnDate = new Date();
    returnDate.setDate(issueDate.getDate() + 7);

    const newIssued = cart.map((book) => ({
      bookId: book._id,
      title: book.title,
      issueDate: issueDate.toISOString().split("T")[0],
      returnDate: returnDate.toISOString().split("T")[0],
    }));

    try {
      await axios.post(`${API_URL}/issued`, { books: newIssued, userEmail });
      setCart([]);
      fetchIssuedBooks();
      alert("Books issued successfully!");
    } catch (err) {
      alert("Error issuing books");
      console.error(err);
    }
  };

  // Calculate fine ₹5/day after return date
  const calculateFine = (returnDate) => {
    const today = new Date();
    const rDate = new Date(returnDate);
    const diffTime = today - rDate;
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
    return diffDays > 0 ? diffDays * 5 : 0;
  };

  // Handle return book (Admin)
  const handleReturnBook = async (issuedBookId) => {
    const book = issuedBooks.find((b) => b._id === issuedBookId);
    if (!book) return;

    const fine = calculateFine(book.returnDate);

    if (fine > 0) {
      const payFine = window.confirm(
        `This book has a fine of ₹${fine}. Please pay before returning.\n\nPay fine now?`
      );
      if (!payFine) {
        alert("Please pay the fine to return the book.");
        return;
      } else {
        alert(`₹${fine} fine paid successfully.`);
      }
    } else {
      alert("Book returned successfully.");
    }

    try {
      await axios.delete(`${API_URL}/issued/${issuedBookId}`);
      fetchIssuedBooks();
    } catch (err) {
      alert("Error returning book");
      console.error(err);
    }
  };

  // Admin: search issued-books by email (calls backend)
  const handleAdminSearchByEmail = () => {
    if (!userSearchEmail) {
      fetchIssuedBooks(); // show all
    } else {
      fetchIssuedBooks(userSearchEmail.trim());
    }
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("role");
    localStorage.removeItem("userEmail");
    alert("You have logged out.");
    window.location.reload();
  };

  // --- UI below ---

  // apply user-side title search (client-side filter)
  const filteredBooks = books.filter((b) =>
    b.title.toLowerCase().includes(titleSearch.trim().toLowerCase())
  );

  if (role === "admin") {
    return (
      <div className="max-w-6xl mx-auto p-6 font-sans">
        <h1 className="text-3xl font-bold mb-6">Admin Panel</h1>

        {/* Admin: Add Book */}
        <div className="mb-8 p-6 bg-white shadow rounded-lg">
          <h2 className="text-2xl font-semibold mb-4">Add New Book</h2>
          <form className="flex flex-col gap-4" onSubmit={addBook}>
            <input
              type="text"
              placeholder="Book Title"
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
              className="border p-2 rounded-md"
            />
            <input
              type="text"
              placeholder="Author Name"
              value={newAuthor}
              onChange={(e) => setNewAuthor(e.target.value)}
              className="border p-2 rounded-md"
            />
            <input
              type="text"
              placeholder="Image URL"
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
              className="border p-2 rounded-md"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-5 py-2 rounded-md"
            >
              Add Book
            </button>
          </form>
        </div>

        {/* Admin: Books list with Edit option */}
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">All Books</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {books.length === 0 ? (
              <p>No books available.</p>
            ) : (
              books.map((book) => (
                <div
                  key={book._id}
                  className="bg-white shadow rounded-lg overflow-hidden"
                >
                  <img
                    src={book.image}
                    alt={book.title}
                    className="h-48 w-full object-cover"
                  />
                  <div className="p-4">
                    <h3 className="font-semibold">{book.title}</h3>
                    <p className="text-sm text-gray-600 mb-2">{book.author}</p>
                    <div className="flex gap-2">
                      <button
                        onClick={() => startEdit(book)}
                        className="px-3 py-1 bg-yellow-500 text-white rounded"
                      >
                        Edit
                      </button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>

          {/* Edit form modal-ish (simple inline) */}
          {editingBook && (
            <div className="mt-6 p-4 bg-gray-50 border rounded">
              <h3 className="font-semibold mb-2">Edit Book</h3>
              <input
                value={editTitle}
                onChange={(e) => setEditTitle(e.target.value)}
                placeholder="Title"
                className="border p-2 rounded w-full mb-2"
              />
              <input
                value={editAuthor}
                onChange={(e) => setEditAuthor(e.target.value)}
                placeholder="Author"
                className="border p-2 rounded w-full mb-2"
              />
              <input
                value={editImage}
                onChange={(e) => setEditImage(e.target.value)}
                placeholder="Image URL"
                className="border p-2 rounded w-full mb-2"
              />
              <div className="flex gap-2">
                <button
                  onClick={saveEdit}
                  className="px-3 py-1 bg-green-600 text-white rounded"
                >
                  Save
                </button>
                <button
                  onClick={cancelEdit}
                  className="px-3 py-1 bg-gray-300 rounded"
                >
                  Cancel
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Admin: Issued books search by email */}
        <div className="mb-6 p-4 bg-white shadow rounded">
          <h2 className="text-xl font-semibold mb-2">
            Search Issued Books by User Email
          </h2>
          <div className="flex gap-2">
            <input
              type="email"
              placeholder="user@example.com"
              value={userSearchEmail}
              onChange={(e) => setUserSearchEmail(e.target.value)}
              className="border p-2 rounded flex-1"
            />
            <button
              onClick={handleAdminSearchByEmail}
              className="px-4 py-2 bg-blue-600 text-white rounded"
            >
              Search
            </button>
            <button
              onClick={() => {
                setUserSearchEmail("");
                fetchIssuedBooks();
              }}
              className="px-4 py-2 bg-gray-300 rounded"
            >
              Reset
            </button>
          </div>
        </div>

        {/* Admin: Issued Books Table */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-2xl font-semibold mb-4">Issued Books</h2>
          {issuedBooks.length === 0 ? (
            <p>No issued books.</p>
          ) : (
            <table className="min-w-full table-auto border-collapse border border-gray-300">
              <thead>
                <tr className="bg-gray-100">
                  <th className="border px-4 py-2">Book Title</th>
                  <th className="border px-4 py-2">Issue Date</th>
                  <th className="border px-4 py-2">Return Date</th>
                  <th className="border px-4 py-2">Fine (₹5/day)</th>
                  <th className="border px-4 py-2">User Email</th>
                  <th className="border px-4 py-2">Actions</th>
                </tr>
              </thead>
              <tbody>
                {issuedBooks.map((book) => {
                  const fine = calculateFine(book.returnDate);
                  return (
                    <tr key={book._id} className="hover:bg-gray-50">
                      <td className="border px-4 py-2">{book.title}</td>
                      <td className="border px-4 py-2">{book.issueDate}</td>
                      <td className="border px-4 py-2">{book.returnDate}</td>
                      <td
                        className={`border px-4 py-2 ${
                          fine > 0 ? "text-red-600 font-bold" : "text-green-600"
                        }`}
                      >
                        ₹{fine}
                      </td>
                      <td className="border px-4 py-2">{book.userEmail}</td>
                      <td className="border px-4 py-2">
                        <button
                          onClick={() => handleReturnBook(book._id)}
                          className="px-3 py-1 bg-yellow-500 text-white rounded"
                        >
                          Return
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>

        <button
          onClick={handleLogout}
          className="mt-6 px-4 py-2 bg-red-600 text-white rounded-md"
        >
          Logout
        </button>
      </div>
    );
  }

  // User UI
  return (
    <div className="max-w-5xl mx-auto p-6 font-sans">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">📚 Book Store</h1>
        <div>
          <button
            onClick={() => setShowIssuedBooks(false)}
            className={`mr-4 px-4 py-2 rounded-md ${
              !showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            Books
          </button>
          <button
            onClick={() => setShowIssuedBooks(true)}
            className={`px-4 py-2 rounded-md ${
              showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            My Issued Books
          </button>
          <button
            onClick={handleLogout}
            className="ml-4 px-4 py-2 bg-red-600 text-white rounded-md"
          >
            Logout
          </button>
        </div>
      </div>

      {!showIssuedBooks && (
        <>
          {/* User: Search bar by title */}
          <div className="mb-6">
            <input
              type="text"
              placeholder="Search books by title..."
              value={titleSearch}
              onChange={(e) => setTitleSearch(e.target.value)}
              className="w-full border p-2 rounded"
            />
          </div>

          {/* Books Listing (filtered by titleSearch) */}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {filteredBooks.length === 0 ? (
              <p className="text-gray-600">No books available currently.</p>
            ) : (
              filteredBooks.map((book) => (
                <div
                  key={book._id}
                  className="bg-white shadow rounded-lg overflow-hidden flex flex-col"
                >
                  <img
                    src={book.image}
                    alt={book.title}
                    className="h-48 w-full object-cover"
                  />
                  <div className="p-4 flex-grow flex flex-col justify-between">
                    <div>
                      <h2 className="font-semibold text-lg">{book.title}</h2>
                      <p className="text-gray-600 text-sm mb-2">
                        {book.author}
                      </p>
                    </div>
                    <button
                      onClick={() => addToCart(book)}
                      disabled={
                        !userCanTakeMoreBooks ||
                        cart.find((c) => c._id === book._id)
                      }
                      className={`mt-2 px-3 py-1 rounded-md text-white ${
                        userCanTakeMoreBooks &&
                        !cart.find((c) => c._id === book._id)
                          ? "bg-blue-600 hover:bg-blue-700"
                          : "bg-gray-400 cursor-not-allowed"
                      }`}
                    >
                      {cart.find((c) => c._id === book._id)
                        ? "Added"
                        : "Add to Cart"}
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>

          {/* Cart */}
          <div className="mt-8">
            <h2 className="text-xl font-semibold mb-2">Cart</h2>
            {cart.length === 0 ? (
              <p>No books in cart.</p>
            ) : (
              <ul>
                {cart.map((book) => (
                  <li
                    key={book._id}
                    className="flex justify-between items-center border p-2 rounded mb-2"
                  >
                    {book.title}
                    <button
                      onClick={() => removeFromCart(book._id)}
                      className="text-red-600 hover:underline"
                    >
                      Remove
                    </button>
                  </li>
                ))}
              </ul>
            )}
            <button
              onClick={issueBooks}
              disabled={cart.length === 0}
              className={`mt-4 px-4 py-2 rounded-md text-white ${
                cart.length > 0
                  ? "bg-green-600 hover:bg-green-700"
                  : "bg-gray-400 cursor-not-allowed"
              }`}
            >
              Issue Books
            </button>
          </div>
        </>
      )}

      {showIssuedBooks && (
        <div>
          <h2 className="text-xl font-semibold mb-4">My Issued Books</h2>
          {issuedBooks.length === 0 ? (
            <p>No books issued currently.</p>
          ) : (
            <table className="min-w-full table-auto border-collapse border border-gray-300">
              <thead>
                <tr className="bg-gray-100">
                  <th className="border px-4 py-2 text-left">Book Title</th>
                  <th className="border px-4 py-2 text-left">Issue Date</th>
                  <th className="border px-4 py-2 text-left">Return Date</th>
                  <th className="border px-4 py-2 text-left">Fine (₹5/day)</th>
                </tr>
              </thead>
              <tbody>
                {issuedBooks.map((book) => {
                  const fine = calculateFine(book.returnDate);
                  return (
                    <tr key={book._id} className="hover:bg-gray-50">
                      <td className="border px-4 py-2">{book.title}</td>
                      <td className="border px-4 py-2">{book.issueDate}</td>
                      <td className="border px-4 py-2">{book.returnDate}</td>
                      <td
                        className={`border px-4 py-2 ${
                          fine > 0 ? "text-red-600 font-bold" : "text-green-600"
                        }`}
                      >
                        ₹{fine}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
};

export default BooksManager;
