import React,{useState,useEffect} from 'react'
import axios from 'axios'

const App = () => {
const [form,setform]=useState({name:'',fatherName:'',mobileNumber:''})
const[data,setData]=useState([])

const API_URL='http://localhost:4000/'

const handleSubmit=(e)=>{
e.preventDefault()
setData([...data,form]);
axios.post(API_URL+'save',form)
}

useEffect(()=>{

const showData=async()=>{
const res=await axios.get(API_URL+'fetchData')
setData(res.data); 
console.log(res.data)
}
showData()
},[])



const handleChange=(e)=>{
  setform({...form,[e.target.name]:e.target.value})
}

const handleDelete=async(id)=>{
  const res=await axios.delete(`${API_URL}delete/${id}`)
setData(data.filter((item)=>item._id==id)); 
}

  return (
    <div style={{textAlign:'center'}}>
  <form onSubmit={handleSubmit}>
    Name:<input type="text" placeholder='Enter The Name' onChange={handleChange} name="name" value={form.name}/><br/>
    Father Name:<input type="text" placeholder='Enter The Father Name'onChange={handleChange} name="fatherName"  value={form.fatherName}/><br/>
    Mobile Number:<input type="text" placeholder='Enter The Mobile Number'onChange={handleChange} name="mobileNumber"  value={form.mobileNumber}/><br/>
    <button type='submit'>submit</button>
  </form>
  
  <h1>Display Detail</h1>

  {data.map((item)=>(
  <div key={item._id}>
  <p>{item.name}</p>
  <p>{item.fatherName}</p>
  <p>{item.mobileNumber}</p>
  <button type="button" onClick={()=>handleDelete(item._id)}>Delete</button>
    </div>
   )) }
    </div>
  )
}

export default App
