import React, { useState } from "react";
import axios from "axios";

export default function App() {
  const [mode, setMode] = useState("userLogin"); // userLogin, adminLogin, signup
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (mode === "signup") {
        await axios.post("http://localhost:5000/api/auth/signup", {
          name: formData.name,
          email: formData.email,
          password: formData.password,
          role: "user", // default user
        });
        alert("Signup Successful!");
      } else {
        const res = await axios.post("http://localhost:5000/api/auth/login", {
          email: formData.email,
          password: formData.password,
        });

        if (mode === "adminLogin" && res.data.role !== "admin") {
          alert("Not an admin account!");
        } else {
          alert(`${res.data.message} (${res.data.role})`);
        }
      }
    } catch (err) {
      alert(err.response?.data?.message || "Something went wrong");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md bg-white p-6 rounded-lg shadow-lg">
        <h1 className="text-2xl font-bold text-center mb-6">
          {mode === "userLogin" && "User Login"}
          {mode === "adminLogin" && "Admin Login"}
          {mode === "signup" && "Signup"}
        </h1>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-4">
          {mode === "signup" && (
            <input
              type="text"
              placeholder="Name"
              value={formData.name}
              onChange={(e) =>
                setFormData({ ...formData, name: e.target.value })
              }
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring focus:border-blue-300"
            />
          )}
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) =>
              setFormData({ ...formData, email: e.target.value })
            }
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring focus:border-blue-300"
          />
          <input
            type="password"
            placeholder="Password"
            value={formData.password}
            onChange={(e) =>
              setFormData({ ...formData, password: e.target.value })
            }
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring focus:border-blue-300"
          />

          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition"
          >
            {mode === "signup" ? "Signup" : "Login"}
          </button>
        </form>

        {/* Switch Links */}
        <div className="mt-6 text-center space-y-2">
          {mode !== "userLogin" && (
            <button
              onClick={() => setMode("userLogin")}
              className="text-blue-500 hover:underline block"
            >
              User Login
            </button>
          )}
          {mode !== "adminLogin" && (
            <button
              onClick={() => setMode("adminLogin")}
              className="text-blue-500 hover:underline block"
            >
              Admin Login
            </button>
          )}
          {mode !== "signup" && (
            <button
              onClick={() => setMode("signup")}
              className="text-blue-500 hover:underline block"
            >
              Signup
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
