import  { useState,useEffect } from "react"
import axios from "axios"
const App = () => {

  const[form ,setForm]=useState({title:"",desc:""})
  const[todo,setTodo]=useState([])
  //const[delId,setDelId]=useState([])

 const api_url='http://localhost:4000/todos'


 useEffect(()=>{

let getTodo=async()=>{

      try{
      let res=await axios.get(api_url)
      setTodo(res.data)
      }catch(err){
        console.log('fetching error',err)
      }
}
getTodo()
 },[])

let  handleSubmit=async(e)=>{
 e.preventDefault()
let res=await axios.post(api_url,form)
 setTodo([...todo,form])
 console.log(res.data)
  }

  let  handleChange=(e)=>{
setForm({...form,[e.target.name]:e.target.value})

  }

  const deleteId=async(id)=>{
try{
   await axios.delete(`${api_url}/${id}`)
}catch(err){
  console.log('error',err)
}
  }

  return (
    <div style={{textAlign:'center'}}>
      <form onSubmit={handleSubmit}>
      <h1>Do-to App</h1>

      Title:<input type="text" placeholder="Enter The Title" name="title" value={form.title} onChange={handleChange}/><br></br>

      description:<input type="text" placeholder="Enter The Description" onChange={handleChange} value={form.desc} name="desc"/><br/>

      <button type="submit" >Save</button><br/>


      </form>

      {todo.length>0?(
        todo.map((item)=>(
          <div key={item._id}>
           <h3>{item.title}</h3>
           <p>{item.desc}</p>

             <button type="button">Edit</button>
            <button type="button" onClick={()=>deleteId(item._id)}>Delete</button>
          </div>
        ))
      )

      :('No todo yet...')}



    </div>


  )


}

export default App
