import React, { useState, useEffect } from "react";

const BooksManager = ({ role }) => {
  // localStorage keys
  const LS_BOOKS = "books";
  const LS_ISSUED_BOOKS = "issuedBooks";
  const LS_CART = "cart";

  // Load from localStorage or fallback initial data
  const getInitialBooks = () => {
    const saved = localStorage.getItem(LS_BOOKS);
    if (saved) return JSON.parse(saved);
    return [
      {
        id: 1,
        title: "JavaScript Mastery",
        author: "John Doe",
        image:
          "https://images.unsplash.com/photo-1512820790803-83ca734da794?auto=format&fit=crop&w=200&q=80",
      },
      {
        id: 2,
        title: "React Basics",
        author: "Jane Smith",
        image:
          "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=200&q=80",
      },
      {
        id: 3,
        title: "Node.js Guide",
        author: "Michael Johnson",
        image:
          "https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?auto=format&fit=crop&w=200&q=80",
      },
    ];
  };

  const getInitialIssuedBooks = () => {
    const saved = localStorage.getItem(LS_ISSUED_BOOKS);
    if (saved) return JSON.parse(saved);
    return [
      {
        id: 1,
        bookId: 1,
        title: "JavaScript Mastery",
        issueDate: "2025-08-01",
        returnDate: "2025-08-08",
      },
      {
        id: 2,
        bookId: 2,
        title: "React Basics",
        issueDate: "2025-07-28",
        returnDate: "2025-08-04",
      },
    ];
  };

  const getInitialCart = () => {
    const saved = localStorage.getItem(LS_CART);
    if (saved) return JSON.parse(saved);
    return [];
  };

  // States
  const [books, setBooks] = useState(getInitialBooks);
  const [issuedBooks, setIssuedBooks] = useState(getInitialIssuedBooks);
  const [cart, setCart] = useState(getInitialCart);

  const [newTitle, setNewTitle] = useState("");
  const [newAuthor, setNewAuthor] = useState("");
  const [newImage, setNewImage] = useState("");

  const [showIssuedBooks, setShowIssuedBooks] = useState(false);
  const [showAdminIssuedBooks, setShowAdminIssuedBooks] = useState(false);

  const MAX_BOOKS_ALLOWED = 5;

  // Save to localStorage whenever books, issuedBooks or cart changes
  useEffect(() => {
    localStorage.setItem(LS_BOOKS, JSON.stringify(books));
  }, [books]);

  useEffect(() => {
    localStorage.setItem(LS_ISSUED_BOOKS, JSON.stringify(issuedBooks));
  }, [issuedBooks]);

  useEffect(() => {
    localStorage.setItem(LS_CART, JSON.stringify(cart));
  }, [cart]);

  // User ‡§ï‡•á ‡§™‡§æ‡§∏ ‡§ï‡§ø‡§§‡§®‡•Ä ‡§¨‡•Å‡§ï‡•ç‡§∏ ‡§Ö‡§≠‡•Ä ‡§π‡•à‡§Ç (issued + cart)
  const totalUserBooks = issuedBooks.length + cart.length;

  const userCanTakeMoreBooks = totalUserBooks < MAX_BOOKS_ALLOWED;
  const booksRemainingForUser = MAX_BOOKS_ALLOWED - totalUserBooks;

  // ‡§®‡§Ø‡§æ ‡§¨‡•Å‡§ï ‡§ú‡•ã‡§°‡§º‡§®‡§æ (Admin)
  const addBook = (e) => {
    e.preventDefault();
    if (!newTitle || !newAuthor || !newImage) {
      alert("Please fill all fields (Title, Author, Image URL)");
      return;
    }
    const newBook = {
      id: books.length > 0 ? Math.max(...books.map((b) => b.id)) + 1 : 1,
      title: newTitle,
      author: newAuthor,
      image: newImage,
    };
    setBooks([...books, newBook]);
    setNewTitle("");
    setNewAuthor("");
    setNewImage("");
  };

  // User ‡§ï‡•á ‡§≤‡§ø‡§è cart ‡§Æ‡•á‡§Ç book add ‡§ï‡§∞‡§®‡§æ
  const addToCart = (book) => {
    if (totalUserBooks >= MAX_BOOKS_ALLOWED) {
      alert("You have already reached maximum limit of 5 books.");
      return;
    }
    if (!cart.find((item) => item.id === book.id)) {
      setCart([...cart, book]);
    }
  };

  // User ‡§ï‡•á ‡§≤‡§ø‡§è cart ‡§∏‡•á book ‡§π‡§ü‡§æ‡§®‡§æ
  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  // 7 ‡§¶‡§ø‡§® ‡§¨‡§æ‡§¶ return date ‡§ï‡•á ‡§∏‡§æ‡§• books issue ‡§ï‡§∞‡§®‡§æ (User)
  const issueBooks = () => {
    if (cart.length === 0) {
      alert("Cart is empty");
      return;
    }
    const issueDate = new Date();
    const returnDate = new Date();
    returnDate.setDate(issueDate.getDate() + 7);

    const newIssued = cart.map((book) => ({
      id: issuedBooks.length + Math.random(),
      bookId: book.id,
      title: book.title,
      issueDate: issueDate.toISOString().split("T")[0],
      returnDate: returnDate.toISOString().split("T")[0],
    }));

    setIssuedBooks([...issuedBooks, ...newIssued]);
    setCart([]);
    alert("Books issued successfully!");
  };

  // Fine calculation (‚Çπ5 per day after returnDate)
  const calculateFine = (returnDate) => {
    const today = new Date();
    const rDate = new Date(returnDate);
    const diffTime = today - rDate;
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
    return diffDays > 0 ? diffDays * 5 : 0;
  };

  // Admin ‡§ï‡•á ‡§≤‡§ø‡§è book return handler (fine pay confirm)
  const handleReturnBook = (issuedBookId) => {
    const book = issuedBooks.find((b) => b.id === issuedBookId);
    if (!book) return;

    const fine = calculateFine(book.returnDate);

    if (fine > 0) {
      const payFine = window.confirm(
        `This book has a fine of ‚Çπ${fine}. Please pay before returning.\n\nPay fine now?`
      );
      if (!payFine) {
        alert("Please pay the fine to return the book.");
        return;
      } else {
        alert(`‚Çπ${fine} fine paid successfully.`);
      }
    } else {
      alert("Book returned successfully.");
    }

    // Issue ‡§∏‡•á ‡§π‡§ü‡§æ‡§®‡§æ (Returned)
    setIssuedBooks(issuedBooks.filter((b) => b.id !== issuedBookId));
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("role");
    alert("You have logged out.");
    window.location.reload();
  };

  // --------- UI ---------

  if (role === "admin") {
    return (
      <div className="max-w-5xl mx-auto p-6 font-sans">
        <h1 className="text-3xl font-bold mb-6">Admin Panel</h1>

        {!userCanTakeMoreBooks && (
          <div className="mb-4 p-4 bg-red-100 text-red-700 rounded border border-red-300">
            User already has 5 books issued/carted. Availability is limited.
          </div>
        )}

        {userCanTakeMoreBooks && (
          <div className="mb-4 p-4 bg-green-100 text-green-700 rounded border border-green-300">
            User can take {booksRemainingForUser} more book
            {booksRemainingForUser > 1 ? "s" : ""}.
          </div>
        )}

        <div className="mb-8 p-6 bg-white shadow rounded-lg">
          <h2 className="text-2xl font-semibold mb-4">Add New Book</h2>
          <form className="flex flex-col gap-4" onSubmit={addBook}>
            <input
              type="text"
              placeholder="Book Title"
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
              className="border p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="text"
              placeholder="Author Name"
              value={newAuthor}
              onChange={(e) => setNewAuthor(e.target.value)}
              className="border p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="text"
              placeholder="Image URL"
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
              className="border p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-5 py-2 rounded-md hover:bg-blue-700 transition"
            >
              Add Book
            </button>
          </form>
        </div>

        <button
          onClick={() => setShowAdminIssuedBooks(!showAdminIssuedBooks)}
          className="mb-4 px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition"
        >
          {showAdminIssuedBooks ? "Hide Issued Books" : "Show Issued Books"}
        </button>

        {showAdminIssuedBooks && (
          <div className="bg-white shadow rounded-lg p-6">
            <h2 className="text-2xl font-semibold mb-4">Issued Books</h2>
            {issuedBooks.length === 0 ? (
              <p>No issued books.</p>
            ) : (
              <table className="min-w-full table-auto border-collapse border border-gray-300">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Book Title
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Issue Date
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Return Date
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Fine (‚Çπ5/day)
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {issuedBooks.map((book) => {
                    const fine = calculateFine(book.returnDate);
                    return (
                      <tr key={book.id} className="hover:bg-gray-50">
                        <td className="border border-gray-300 px-4 py-2">
                          {book.title}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          {book.issueDate}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          {book.returnDate}
                        </td>
                        <td
                          className={`border border-gray-300 px-4 py-2 ${
                            fine > 0
                              ? "text-red-600 font-bold"
                              : "text-green-600"
                          }`}
                        >
                          ‚Çπ{fine}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          <button
                            onClick={() => handleReturnBook(book.id)}
                            className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded-md transition"
                          >
                            Return Book
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            )}
          </div>
        )}

        <button
          onClick={handleLogout}
          className="mt-6 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
        >
          Logout
        </button>
      </div>
    );
  }

  // User view
  return (
    <div className="max-w-5xl mx-auto p-6 font-sans">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">üìö Book Store</h1>
        <div>
          <button
            onClick={() => setShowIssuedBooks(false)}
            className={`mr-4 px-4 py-2 rounded-md transition ${
              !showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            Books
          </button>
          <button
            onClick={() => setShowIssuedBooks(true)}
            className={`px-4 py-2 rounded-md transition ${
              showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            My Issued Books
          </button>
          <button
            onClick={handleLogout}
            className="ml-4 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
          >
            Logout
          </button>
        </div>
      </div>

      {!showIssuedBooks && (
        <>
          {/* Books Listing */}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {books.length === 0 ? (
              <p className="text-gray-600">No books available currently.</p>
            ) : (
              books.map((book) => (
                <div
                  key={book.id}
                  className="border rounded-lg p-5 shadow hover:shadow-lg transition bg-white"
                >
                  <img
                    src={book.image}
                    alt={book.title}
                    className="w-full h-40 object-cover rounded mb-3"
                  />
                  <h3 className="text-xl font-semibold mb-1 text-gray-900">
                    {book.title}
                  </h3>
                  <p className="mb-2 text-gray-700">By {book.author}</p>
                  <p className="mb-2 text-gray-700">
                    ‚Çπ5 per day fine after expiry
                  </p>
                  <button
                    onClick={() => addToCart(book)}
                    disabled={cart.find((item) => item.id === book.id)}
                    className={`w-full py-2 rounded-md text-white transition ${
                      cart.find((item) => item.id === book.id)
                        ? "bg-gray-400 cursor-not-allowed"
                        : "bg-blue-600 hover:bg-blue-700"
                    }`}
                  >
                    {cart.find((item) => item.id === book.id)
                      ? "Added to Cart"
                      : "Add to Cart"}
                  </button>
                </div>
              ))
            )}
          </div>

          {/* Cart Section */}
          <h2 className="text-2xl font-semibold mt-12 mb-6 text-gray-800">
            üõí Your Cart
          </h2>

          {cart.length === 0 ? (
            <p className="text-gray-600">Cart is empty</p>
          ) : (
            <div className="space-y-4">
              {cart.map((item) => (
                <div
                  key={item.id}
                  className="flex justify-between items-center bg-gray-100 p-4 rounded-md shadow"
                >
                  <span className="text-gray-900 font-medium">
                    {item.title}
                  </span>
                  <button
                    onClick={() => removeFromCart(item.id)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md transition"
                  >
                    Remove
                  </button>
                </div>
              ))}

              <button
                onClick={issueBooks}
                className="mt-6 bg-green-600 hover:bg-green-700 text-white py-3 px-6 rounded-md font-semibold transition"
              >
                Issue Books
              </button>
            </div>
          )}
        </>
      )}

      {/* My Issued Books */}
      {showIssuedBooks && (
        <>
          <h2 className="text-2xl font-semibold mt-12 mb-6 text-gray-800">
            üìö My Issued Books
          </h2>
          {issuedBooks.length === 0 ? (
            <p className="text-gray-600">You have no issued books.</p>
          ) : (
            <div className="space-y-4">
              {issuedBooks.map((book) => {
                const fine = calculateFine(book.returnDate);
                return (
                  <div
                    key={book.id}
                    className="bg-gray-100 p-4 rounded-md shadow flex flex-col sm:flex-row justify-between items-center"
                  >
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">
                        {book.title}
                      </h3>
                      <p>Issue Date: {book.issueDate}</p>
                      <p>Return Date: {book.returnDate}</p>
                    </div>
                    <div className="mt-2 sm:mt-0 text-right">
                      <p>
                        Fine:{" "}
                        <span
                          className={
                            fine > 0 ? "text-red-600" : "text-green-600"
                          }
                        >
                          ‚Çπ{fine}
                        </span>
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default BooksManager;
