import React ,{useState} from 'react'

const App = () => {
const [form,setform]=useState({name:'',fatherName:'',mobileNumber:''})
const[data,setData]=useState([])

const handleSubmit=(e)=>{
e.preventDefault()
setData([...data,form]);
}

const handleChange=(e)=>{
  setform({...form,[e.target.name]:e.target.value})
}


  return (
    <div style={{textAlign:'center'}}>
  <form onSubmit={handleSubmit}>
    Name:<input type="text" placeholder='Enter The Name' onChange={handleChange} name="name" value={form.name}/><br/>
    Father Name:<input type="text" placeholder='Enter The Father Name'onChange={handleChange} name="fatherName"  value={form.fatherName}/><br/>
    Mobile Number:<input type="text" placeholder='Enter The Mobile Number'onChange={handleChange} name="mobileNumber"  value={form.mobileNumber}/><br/>
    <button type='submit'>submit</button>
  </form>
  
  <h1>Display Detail</h1>
  <p>{data.name}</p>
   <p>{data.fatherName}</p>
    <p>{data.mobileNumber}</p>
    </div>
  )
}

export default App
