import React, { useState } from "react";

const BooksComponent = () => {
  const [cart, setCart] = useState([]);
  const [purchasedBooks, setPurchasedBooks] = useState([]);
  const [showPurchases, setShowPurchases] = useState(false);

  const books = [
    { id: 1, title: "JavaScript Mastery", price: 499 },
    { id: 2, title: "React Basics", price: 399 },
    { id: 3, title: "Node.js Guide", price: 599 },
    { id: 4, title: "CSS Essentials", price: 299 },
  ];

  const addToCart = (book) => {
    if (cart.length >= 3) {
      alert("You can add maximum 3 books to the cart.");
      return;
    }
    if (!cart.find((item) => item.id === book.id)) {
      setCart([...cart, book]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  // 7 दिन बाद return date के साथ purchasedBooks में add करेगा
  const purchase = () => {
    if (cart.length === 0) {
      alert("Cart is empty");
      return;
    }

    const purchaseDate = new Date();
    const returnDate = new Date();
    returnDate.setDate(purchaseDate.getDate() + 7); // 7 दिन बाद return date

    const newPurchasedBooks = cart.map((book) => ({
      ...book,
      purchaseDate: purchaseDate.toISOString().split("T")[0], // yyyy-mm-dd
      returnDate: returnDate.toISOString().split("T")[0],
    }));

    setPurchasedBooks([...purchasedBooks, ...newPurchasedBooks]);
    setCart([]);
    alert("Purchase successful!");
  };

  // fine calculation: (आज की तारीख - returnDate) * 5 अगर overdue हो तो
  const calculateFine = (returnDate) => {
    const today = new Date();
    const rDate = new Date(returnDate);
    const diffTime = today - rDate; // milliseconds difference
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));

    return diffDays > 0 ? diffDays * 5 : 0;
  };

  const handleLogout = () => {
    localStorage.removeItem("role");
    alert("You have logged out.");
    window.location.reload();
  };

  return (
    <div className="max-w-5xl mx-auto p-6 font-sans">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">📚 Book Store</h1>
        <div>
          <button
            onClick={() => setShowPurchases(false)}
            className={`mr-4 px-4 py-2 rounded-md transition ${
              !showPurchases
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            Books
          </button>
          <button
            onClick={() => setShowPurchases(true)}
            className={`px-4 py-2 rounded-md transition ${
              showPurchases
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            My Purchases
          </button>
          <button
            onClick={handleLogout}
            className="ml-4 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
          >
            Logout
          </button>
        </div>
      </div>

      {!showPurchases && (
        <>
          {/* Books Listing */}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {books.map((book) => (
              <div
                key={book.id}
                className="border rounded-lg p-5 shadow hover:shadow-lg transition bg-white"
              >
                <h3 className="text-xl font-semibold mb-2 text-gray-900">
                  {book.title}
                </h3>
                <p className="text-gray-700 mb-4">₹{book.price}</p>
                <button
                  onClick={() => addToCart(book)}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-md transition"
                >
                  Add to Cart
                </button>
              </div>
            ))}
          </div>

          {/* Cart Section */}
          <h2 className="text-2xl font-semibold mt-12 mb-6 text-gray-800">
            🛒 Your Cart
          </h2>

          {cart.length === 0 ? (
            <p className="text-gray-600">Cart is empty</p>
          ) : (
            <div className="space-y-4">
              {cart.map((item) => (
                <div
                  key={item.id}
                  className="flex justify-between items-center bg-gray-100 p-4 rounded-md shadow"
                >
                  <span className="text-gray-900 font-medium">
                    {item.title} - ₹{item.price}
                  </span>
                  <button
                    onClick={() => removeFromCart(item.id)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md transition"
                  >
                    Remove
                  </button>
                </div>
              ))}

              <button
                onClick={purchase}
                className="mt-6 bg-green-600 hover:bg-green-700 text-white py-3 px-6 rounded-md font-semibold transition"
              >
                Purchase
              </button>
            </div>
          )}
        </>
      )}

      {/* My Purchased Books */}
      {showPurchases && (
        <>
          <h2 className="text-2xl font-semibold mt-12 mb-6 text-gray-800">
            📚 My Purchased Books
          </h2>
          {purchasedBooks.length === 0 ? (
            <p className="text-gray-600">You have no purchased books.</p>
          ) : (
            <div className="space-y-4">
              {purchasedBooks.map((book, index) => {
                const fine = calculateFine(book.returnDate);
                return (
                  <div
                    key={index}
                    className="bg-gray-100 p-4 rounded-md shadow flex flex-col sm:flex-row justify-between items-center"
                  >
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">
                        {book.title}
                      </h3>
                      <p>Purchase Date: {book.purchaseDate}</p>
                      <p>Return Date: {book.returnDate}</p>
                    </div>
                    <div className="mt-2 sm:mt-0 text-right">
                      <p>
                        Fine:{" "}
                        <span className={fine > 0 ? "text-red-600" : "text-green-600"}>
                          ₹{fine}
                        </span>
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default BooksComponent;
