import React,{useState,useEffect} from 'react'
import axios from 'axios'
const App = () => {
const [form,setform]=useState({name:'',fatherName:'',mobileNumber:''})
const[data,setData]=useState([])

const API_URL=' http://localhost:4000/'

const handleSubmit=(e)=>{
e.preventDefault()
setData([...data,form]);
axios.post(API_URL+'save',form)
}

const handleChange=(e)=>{
  setform({...form,[e.target.name]:e.target.value})
}


  return (
    <div style={{textAlign:'center'}}>
  <form onSubmit={handleSubmit}>
    Name:<input type="text" placeholder='Enter The Name' onChange={handleChange} name="name" value={form.name}/><br/>
    Father Name:<input type="text" placeholder='Enter The Father Name'onChange={handleChange} name="fatherName"  value={form.fatherName}/><br/>
    Mobile Number:<input type="text" placeholder='Enter The Mobile Number'onChange={handleChange} name="mobileNumber"  value={form.mobileNumber}/><br/>
    <button type='submit'>submit</button>
  </form>
  
  <h1>Display Detail</h1>

  {data.map((item,index)=>(
  <div key={index}>
  <p>{item.name}</p>
  <p>{item.fatherName}</p>
  <p>{item.mobileNumber}</p>
    </div>
   )) }
    </div>
  )
}

export default App
