import React, { useState } from "react";

const AdminPanel = () => {
  const [books, setBooks] = useState([
    { id: 1, title: "JavaScript Mastery", price: 499 },
    { id: 2, title: "React Basics", price: 399 },
    { id: 3, title: "Node.js Guide", price: 599 },
  ]);

  const [purchasedBooks, setPurchasedBooks] = useState([
    {
      id: 1,
      bookId: 1,
      title: "JavaScript Mastery",
      userId: 101,
      username: "Ramesh",
      purchaseDate: "2025-08-01",
      returnDate: "2025-08-08",
      fine: 0,
    },
    {
      id: 2,
      bookId: 2,
      title: "React Basics",
      userId: 102,
      username: "Suresh",
      purchaseDate: "2025-07-28",
      returnDate: "2025-08-04",
      fine: 15,
    },
  ]);

  const [newTitle, setNewTitle] = useState("");
  const [newPrice, setNewPrice] = useState("");

  const addBook = (e) => {
    e.preventDefault();
    if (!newTitle || !newPrice) {
      alert("Please fill all fields");
      return;
    }
    const newBook = {
      id: books.length + 1,
      title: newTitle,
      price: Number(newPrice),
    };
    setBooks([...books, newBook]);
    setNewTitle("");
    setNewPrice("");
  };

  // Return book handler
  const handleReturnBook = (bookId) => {
    const book = purchasedBooks.find((pb) => pb.id === bookId);
    if (!book) return;

    if (book.fine > 0) {
      const payFine = window.confirm(
        `This book has a fine of ₹${book.fine}. Please pay before returning.\n\nPay fine now?`
      );
      if (!payFine) {
        alert("Please pay the fine to return the book.");
        return;
      } else {
        alert(`₹${book.fine} fine paid successfully.`);
        // Here you can add payment API call logic
      }
    }

    // Remove the returned book from purchasedBooks
    setPurchasedBooks(purchasedBooks.filter((pb) => pb.id !== bookId));
    alert(`Book "${book.title}" returned successfully.`);
  };

  const today = new Date();

  const activeUsers = new Set(
    purchasedBooks
      .filter((p) => new Date(p.returnDate) >= today)
      .map((p) => p.userId)
  );

  const totalPurchasedBooks = purchasedBooks.length;

  const totalFines = purchasedBooks.reduce((acc, curr) => acc + curr.fine, 0);

  return (
    <div className="max-w-7xl mx-auto p-6 font-sans">
      <h1 className="text-4xl font-bold mb-6 text-gray-800">Admin Panel</h1>

      {/* Add Book Form */}
      <div className="mb-10 p-6 bg-white shadow rounded-lg">
        <h2 className="text-2xl font-semibold mb-4">Add New Book</h2>
        <form className="flex gap-4" onSubmit={addBook}>
          <input
            type="text"
            placeholder="Book Title"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
            className="border p-2 rounded-md flex-1 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="number"
            placeholder="Price (₹)"
            value={newPrice}
            onChange={(e) => setNewPrice(e.target.value)}
            className="border p-2 rounded-md w-32 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="bg-blue-600 text-white px-5 py-2 rounded-md hover:bg-blue-700 transition"
          >
            Add Book
          </button>
        </form>
      </div>

      {/* Summary */}
      <div className="mb-10 grid grid-cols-1 sm:grid-cols-3 gap-6">
        <div className="bg-white p-6 shadow rounded-lg text-center">
          <h3 className="text-xl font-semibold mb-2">Total Books Purchased</h3>
          <p className="text-3xl font-bold">{totalPurchasedBooks}</p>
        </div>
        <div className="bg-white p-6 shadow rounded-lg text-center">
          <h3 className="text-xl font-semibold mb-2">Active Users</h3>
          <p className="text-3xl font-bold">{activeUsers.size}</p>
        </div>
        <div className="bg-white p-6 shadow rounded-lg text-center">
          <h3 className="text-xl font-semibold mb-2">Total Fines</h3>
          <p className="text-3xl font-bold">₹{totalFines}</p>
        </div>
      </div>

      {/* Purchased Books Table */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-2xl font-semibold mb-4">Purchased Books Details</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full table-auto border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="border border-gray-300 px-4 py-2 text-left">User</th>
                <th className="border border-gray-300 px-4 py-2 text-left">Book</th>
                <th className="border border-gray-300 px-4 py-2 text-left">Purchase Date</th>
                <th className="border border-gray-300 px-4 py-2 text-left">Return Date</th>
                <th className="border border-gray-300 px-4 py-2 text-left">Fine (₹)</th>
                <th className="border border-gray-300 px-4 py-2 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {purchasedBooks.length === 0 && (
                <tr>
                  <td colSpan="6" className="text-center p-4 text-gray-600">
                    No purchased books data found.
                  </td>
                </tr>
              )}
              {purchasedBooks.map((pb) => (
                <tr key={pb.id} className="hover:bg-gray-50">
                  <td className="border border-gray-300 px-4 py-2">{pb.username}</td>
                  <td className="border border-gray-300 px-4 py-2">{pb.title}</td>
                  <td className="border border-gray-300 px-4 py-2">{pb.purchaseDate}</td>
                  <td className="border border-gray-300 px-4 py-2">{pb.returnDate}</td>
                  <td
                    className={`border border-gray-300 px-4 py-2 ${
                      pb.fine > 0 ? "text-red-600 font-bold" : "text-green-600"
                    }`}
                  >
                    ₹{pb.fine}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    <button
                      onClick={() => handleReturnBook(pb.id)}
                      className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded-md transition"
                    >
                      Return Book
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
