import React, { useState } from "react";

const BooksManager = ({ role }) => {
  // à¤¸à¤­à¥€ à¤¬à¥à¤•à¥à¤¸ (Admin à¤•à¥‡ à¤²à¤¿à¤)
  const [books, setBooks] = useState([
    {
      id: 1,
      title: "JavaScript Mastery",
      author: "John Doe",
      image:
        "https://images.unsplash.com/photo-1512820790803-83ca734da794?auto=format&fit=crop&w=200&q=80",
    },
    {
      id: 2,
      title: "React Basics",
      author: "Jane Smith",
      image:
        "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=200&q=80",
    },
    {
      id: 3,
      title: "Node.js Guide",
      author: "Michael Johnson",
      image:
        "https://images.unsplash.com/photo-1524995997946-a1c2e315a42f?auto=format&fit=crop&w=200&q=80",
    },
  ]);

  // Issue à¤•à¥€ à¤—à¤ˆ à¤•à¤¿à¤¤à¤¾à¤¬à¥‡à¤‚ (Admin à¤”à¤° User à¤¦à¥‹à¤¨à¥‹à¤‚ à¤•à¥‡ à¤²à¤¿à¤)
  const [issuedBooks, setIssuedBooks] = useState([
    {
      id: 1,
      bookId: 1,
      title: "JavaScript Mastery",
      issueDate: "2025-08-01",
      returnDate: "2025-08-08",
    },
    {
      id: 2,
      bookId: 2,
      title: "React Basics",
      issueDate: "2025-07-28",
      returnDate: "2025-08-04",
    },
  ]);

  // Admin à¤•à¥‡ à¤²à¤¿à¤ à¤¨à¤¯à¤¾ à¤¬à¥à¤• add à¤•à¤°à¤¨à¥‡ à¤•à¥‡ à¤²à¤¿à¤ states
  const [newTitle, setNewTitle] = useState("");
  const [newAuthor, setNewAuthor] = useState("");
  const [newImage, setNewImage] = useState("");

  // User cart (à¤¬à¥à¤• à¤²à¥‡à¤¨à¥‡ à¤•à¥‡ à¤²à¤¿à¤)
  const [cart, setCart] = useState([]);

  // User à¤•à¥‡ à¤²à¤¿à¤ issuedBooks à¤¦à¤¿à¤–à¤¾à¤¨à¥‡ à¤•à¤¾ toggle
  const [showIssuedBooks, setShowIssuedBooks] = useState(false);

  // Admin à¤•à¥‡ à¤²à¤¿à¤ issuedBooks à¤¦à¤¿à¤–à¤¾à¤¨à¥‡ à¤•à¤¾ toggle (optional)
  const [showAdminIssuedBooks, setShowAdminIssuedBooks] = useState(false);

  // à¤¨à¤¯à¤¾ à¤¬à¥à¤• à¤œà¥‹à¤¡à¤¼à¤¨à¤¾ (Admin)
  const addBook = (e) => {
    e.preventDefault();
    if (!newTitle || !newAuthor || !newImage) {
      alert("Please fill all fields (Title, Author, Image URL)");
      return;
    }
    const newBook = {
      id: books.length + 1,
      title: newTitle,
      author: newAuthor,
      image: newImage,
    };
    setBooks([...books, newBook]);
    setNewTitle("");
    setNewAuthor("");
    setNewImage("");
  };

  // User à¤•à¥‡ à¤²à¤¿à¤ cart à¤®à¥‡à¤‚ book add à¤•à¤°à¤¨à¤¾
  const addToCart = (book) => {
    if (cart.length >= 3) {
      alert("You can add maximum 3 books to the cart.");
      return;
    }
    if (!cart.find((item) => item.id === book.id)) {
      setCart([...cart, book]);
    }
  };

  // User à¤•à¥‡ à¤²à¤¿à¤ cart à¤¸à¥‡ book à¤¹à¤Ÿà¤¾à¤¨à¤¾
  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  // 7 à¤¦à¤¿à¤¨ à¤¬à¤¾à¤¦ return date à¤•à¥‡ à¤¸à¤¾à¤¥ books issue à¤•à¤°à¤¨à¤¾ (User)
  const issueBooks = () => {
    if (cart.length === 0) {
      alert("Cart is empty");
      return;
    }
    const issueDate = new Date();
    const returnDate = new Date();
    returnDate.setDate(issueDate.getDate() + 7);

    const newIssued = cart.map((book) => ({
      id: issuedBooks.length + Math.random(), // unique id
      bookId: book.id,
      title: book.title,
      issueDate: issueDate.toISOString().split("T")[0],
      returnDate: returnDate.toISOString().split("T")[0],
    }));

    setIssuedBooks([...issuedBooks, ...newIssued]);
    setCart([]);
    alert("Books issued successfully!");
  };

  // Fine calculation (â‚¹5 per day after returnDate)
  const calculateFine = (returnDate) => {
    const today = new Date();
    const rDate = new Date(returnDate);
    const diffTime = today - rDate;
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
    return diffDays > 0 ? diffDays * 5 : 0;
  };

  // Admin à¤•à¥‡ à¤²à¤¿à¤ book return handler (fine pay confirm)
  const handleReturnBook = (issuedBookId) => {
    const book = issuedBooks.find((b) => b.id === issuedBookId);
    if (!book) return;

    const fine = calculateFine(book.returnDate);

    if (fine > 0) {
      const payFine = window.confirm(
        `This book has a fine of â‚¹${fine}. Please pay before returning.\n\nPay fine now?`
      );
      if (!payFine) {
        alert("Please pay the fine to return the book.");
        return;
      } else {
        alert(`â‚¹${fine} fine paid successfully.`);
      }
    } else {
      alert("Book returned successfully.");
    }

    // Issue à¤¸à¥‡ à¤¹à¤Ÿà¤¾à¤¨à¤¾ (Returned)
    setIssuedBooks(issuedBooks.filter((b) => b.id !== issuedBookId));
  };

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("role");
    alert("You have logged out.");
    window.location.reload();
  };

  // --------- UI ---------

  if (role === "admin") {
    return (
      <div className="max-w-5xl mx-auto p-6 font-sans">
        <h1 className="text-3xl font-bold mb-6">Admin Panel</h1>

        {/* Add Book Form */}
        <div className="mb-8 p-6 bg-white shadow rounded-lg">
          <h2 className="text-2xl font-semibold mb-4">Add New Book</h2>
          <form className="flex flex-col gap-4" onSubmit={addBook}>
            <input
              type="text"
              placeholder="Book Title"
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
              className="border p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="text"
              placeholder="Author Name"
              value={newAuthor}
              onChange={(e) => setNewAuthor(e.target.value)}
              className="border p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="text"
              placeholder="Image URL"
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
              className="border p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-5 py-2 rounded-md hover:bg-blue-700 transition"
            >
              Add Book
            </button>
          </form>
        </div>

        {/* Available Books */}
        <div className="mb-8 bg-white shadow rounded-lg p-6">
          <h2 className="text-2xl font-semibold mb-4">Available Books</h2>
          {books.length === 0 ? (
            <p>No books available.</p>
          ) : (
            <ul>
              {books.map((book) => (
                <li
                  key={book.id}
                  className="mb-3 p-3 border rounded shadow-sm flex items-center gap-4"
                >
                  <img
                    src={book.image}
                    alt={book.title}
                    className="w-16 h-20 object-cover rounded"
                  />
                  <div>
                    <h3 className="text-lg font-semibold">{book.title}</h3>
                    <p className="text-gray-600">By {book.author}</p>
                    <p className="text-gray-700">
                      â‚¹5 per day fine after expiry
                    </p>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Toggle issued books list */}
        <button
          onClick={() => setShowAdminIssuedBooks(!showAdminIssuedBooks)}
          className="mb-4 px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition"
        >
          {showAdminIssuedBooks ? "Hide Issued Books" : "Show Issued Books"}
        </button>

        {/* Issued Books list with Return button */}
        {showAdminIssuedBooks && (
          <div className="bg-white shadow rounded-lg p-6">
            <h2 className="text-2xl font-semibold mb-4">Issued Books</h2>
            {issuedBooks.length === 0 ? (
              <p>No issued books.</p>
            ) : (
              <table className="min-w-full table-auto border-collapse border border-gray-300">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Book Title
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Issue Date
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Return Date
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Fine (â‚¹5/day)
                    </th>
                    <th className="border border-gray-300 px-4 py-2 text-left">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {issuedBooks.map((book) => {
                    const fine = calculateFine(book.returnDate);
                    return (
                      <tr key={book.id} className="hover:bg-gray-50">
                        <td className="border border-gray-300 px-4 py-2">
                          {book.title}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          {book.issueDate}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          {book.returnDate}
                        </td>
                        <td
                          className={`border border-gray-300 px-4 py-2 ${
                            fine > 0
                              ? "text-red-600 font-bold"
                              : "text-green-600"
                          }`}
                        >
                          â‚¹{fine}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          <button
                            onClick={() => handleReturnBook(book.id)}
                            className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded-md transition"
                          >
                            Return Book
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            )}
          </div>
        )}

        <button
          onClick={handleLogout}
          className="mt-6 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
        >
          Logout
        </button>
      </div>
    );
  }

  // User view
  return (
    <div className="max-w-5xl mx-auto p-6 font-sans">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">ðŸ“š Book Store</h1>
        <div>
          <button
            onClick={() => setShowIssuedBooks(false)}
            className={`mr-4 px-4 py-2 rounded-md transition ${
              !showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            Books
          </button>
          <button
            onClick={() => setShowIssuedBooks(true)}
            className={`px-4 py-2 rounded-md transition ${
              showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            My Issued Books
          </button>
          <button
            onClick={handleLogout}
            className="ml-4 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
          >
            Logout
          </button>
        </div>
      </div>

      {!showIssuedBooks && (
        <>
          {/* Books Listing */}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {books.map((book) => (
              <div
                key={book.id}
                className="border rounded-lg p-5 shadow hover:shadow-lg transition bg-white"
              >
                <h3 className="text-xl font-semibold mb-2 text-gray-900">
                  {book.title}
                </h3>
                <p className="mb-2 text-gray-700">
                  â‚¹5 per day fine after expiry
                </p>
                <button
                  onClick={() => addToCart(book)}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-md transition"
                >
                  Add to Cart
                </button>
              </div>
            ))}
          </div>

          {/* Cart Section */}
          <h2 className="text-2xl font-semibold mt-12 mb-6 text-gray-800">
            ðŸ›’ Your Cart
          </h2>

          {cart.length === 0 ? (
            <p className="text-gray-600">Cart is empty</p>
          ) : (
            <div className="space-y-4">
              {cart.map((item) => (
                <div
                  key={item.id}
                  className="flex justify-between items-center bg-gray-100 p-4 rounded-md shadow"
                >
                  <span className="text-gray-900 font-medium">
                    {item.title}
                  </span>
                  <button
                    onClick={() => removeFromCart(item.id)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md transition"
                  >
                    Remove
                  </button>
                </div>
              ))}

              <button
                onClick={issueBooks}
                className="mt-6 bg-green-600 hover:bg-green-700 text-white py-3 px-6 rounded-md font-semibold transition"
              >
                Issue Books
              </button>
            </div>
          )}
        </>
      )}

      {/* My Issued Books */}
      {showIssuedBooks && (
        <>
          <h2 className="text-2xl font-semibold mt-12 mb-6 text-gray-800">
            ðŸ“š My Issued Books
          </h2>
          {issuedBooks.length === 0 ? (
            <p className="text-gray-600">You have no issued books.</p>
          ) : (
            <div className="space-y-4">
              {issuedBooks.map((book) => {
                const fine = calculateFine(book.returnDate);
                return (
                  <div
                    key={book.id}
                    className="bg-gray-100 p-4 rounded-md shadow flex flex-col sm:flex-row justify-between items-center"
                  >
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">
                        {book.title}
                      </h3>
                      <p>Issue Date: {book.issueDate}</p>
                      <p>Return Date: {book.returnDate}</p>
                    </div>
                    <div className="mt-2 sm:mt-0 text-right">
                      <p>
                        Fine:{" "}
                        <span
                          className={
                            fine > 0 ? "text-red-600" : "text-green-600"
                          }
                        >
                          â‚¹{fine}
                        </span>
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default BooksManager;
