import React, { useState } from "react";

const BooksManager = ({ role }) => {
  // सभी बुक्स (Admin के लिए)
  const [books, setBooks] = useState([
    { id: 1, title: "JavaScript Mastery", price: 499 },
    { id: 2, title: "React Basics", price: 399 },
    { id: 3, title: "Node.js Guide", price: 599 },
  ]);

  // User के लिए issue की गई किताबें (Issued Books)
  const [issuedBooks, setIssuedBooks] = useState([
    {
      id: 1,
      bookId: 1,
      title: "JavaScript Mastery",
      issueDate: "2025-08-01",
      returnDate: "2025-08-08",
    },
    {
      id: 2,
      bookId: 2,
      title: "React Basics",
      issueDate: "2025-07-28",
      returnDate: "2025-08-04",
    },
  ]);

  // Admin के लिए नए बुक add करने के लिए states
  const [newTitle, setNewTitle] = useState("");
  const [newPrice, setNewPrice] = useState("");

  // User cart (बुक खरीदने के लिए)
  const [cart, setCart] = useState([]);

  // User के लिए showIssuedBooks toggle
  const [showIssuedBooks, setShowIssuedBooks] = useState(false);

  // नया बुक जोड़ना (Admin)
  const addBook = (e) => {
    e.preventDefault();
    if (!newTitle || !newPrice) {
      alert("Please fill all fields");
      return;
    }
    const newBook = {
      id: books.length + 1,
      title: newTitle,
      price: Number(newPrice),
    };
    setBooks([...books, newBook]);
    setNewTitle("");
    setNewPrice("");
  };

  // User के लिए cart में book add करना
  const addToCart = (book) => {
    if (cart.length >= 3) {
      alert("You can add maximum 3 books to the cart.");
      return;
    }
    if (!cart.find((item) => item.id === book.id)) {
      setCart([...cart, book]);
    }
  };

  // User के लिए cart से book हटाना
  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  // 7 दिन बाद return date के साथ books issue करना (User)
  const issueBooks = () => {
    if (cart.length === 0) {
      alert("Cart is empty");
      return;
    }
    const issueDate = new Date();
    const returnDate = new Date();
    returnDate.setDate(issueDate.getDate() + 7);

    const newIssued = cart.map((book) => ({
      ...book,
      issueDate: issueDate.toISOString().split("T")[0],
      returnDate: returnDate.toISOString().split("T")[0],
    }));

    setIssuedBooks([...issuedBooks, ...newIssued]);
    setCart([]);
    alert("Books issued successfully!");
  };

  // Fine calculation (User)
  const calculateFine = (returnDate) => {
    const today = new Date();
    const rDate = new Date(returnDate);
    const diffTime = today - rDate;
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
    return diffDays > 0 ? diffDays * 5 : 0;
  };

  // Logout button handler
  const handleLogout = () => {
    localStorage.removeItem("role");
    alert("You have logged out.");
    window.location.reload();
  };

  // --------- UI ---------

  // Admin view
  if (role === "admin") {
    return (
      <div className="max-w-5xl mx-auto p-6 font-sans">
        <h1 className="text-3xl font-bold mb-6">Admin Panel</h1>

        {/* Add Book Form */}
        <div className="mb-8 p-6 bg-white shadow rounded-lg">
          <h2 className="text-2xl font-semibold mb-4">Add New Book</h2>
          <form className="flex gap-4" onSubmit={addBook}>
            <input
              type="text"
              placeholder="Book Title"
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
              className="border p-2 rounded-md flex-1 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="number"
              placeholder="Price (₹)"
              value={newPrice}
              onChange={(e) => setNewPrice(e.target.value)}
              className="border p-2 rounded-md w-32 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-5 py-2 rounded-md hover:bg-blue-700 transition"
            >
              Add Book
            </button>
          </form>
        </div>

        {/* Books List */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-2xl font-semibold mb-4">Available Books</h2>
          {books.length === 0 ? (
            <p>No books available.</p>
          ) : (
            <ul>
              {books.map((book) => (
                <li
                  key={book.id}
                  className="mb-3 p-3 border rounded shadow-sm flex justify-between items-center"
                >
                  <span className="text-lg font-semibold">{book.title}</span>
                  <span className="text-gray-700">₹{book.price}</span>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    );
  }

  // User view
  return (
    <div className="max-w-5xl mx-auto p-6 font-sans">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">📚 Book Store</h1>
        <div>
          <button
            onClick={() => setShowIssuedBooks(false)}
            className={`mr-4 px-4 py-2 rounded-md transition ${
              !showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            Books
          </button>
          <button
            onClick={() => setShowIssuedBooks(true)}
            className={`px-4 py-2 rounded-md transition ${
              showIssuedBooks
                ? "bg-blue-600 text-white"
                : "bg-gray-300 text-gray-700"
            }`}
          >
            My Issued Books
          </button>
          <button
            onClick={handleLogout}
            className="ml-4 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
          >
            Logout
          </button>
        </div>
      </div>

      {!showIssuedBooks && (
        <>
          {/* Books Listing */}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {books.map((book) => (
              <div
                key={book.id}
                className="border rounded-lg p-5 shadow hover:shadow-lg transition bg-white"
              >
                <h3 className="text-xl font-semibold mb-2 text-gray-900">
                  {book.title}
                </h3>
                <p className="mb-2 text-gray-700">Price: ₹{book.price}</p>
                <button
                  onClick={() => addToCart(book)}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-md transition"
                >
                  Add to Cart
                </button>
              </div>
            ))}
          </div>

          {/* Cart Section */}
          <h2 className="text-2xl font-semibold mt-12 mb-6 text-gray-800">
            🛒 Your Cart
          </h2>

          {cart.length === 0 ? (
            <p className="text-gray-600">Cart is empty</p>
          ) : (
            <div className="space-y-4">
              {cart.map((item) => (
                <div
                  key={item.id}
                  className="flex justify-between items-center bg-gray-100 p-4 rounded-md shadow"
                >
                  <span className="text-gray-900 font-medium">
                    {item.title}
                  </span>
                  <button
                    onClick={() => removeFromCart(item.id)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md transition"
                  >
                    Remove
                  </button>
                </div>
              ))}

              <button
                onClick={issueBooks}
                className="mt-6 bg-green-600 hover:bg-green-700 text-white py-3 px-6 rounded-md font-semibold transition"
              >
                Issue Books
              </button>
            </div>
          )}
        </>
      )}

      {/* My Issued Books */}
      {showIssuedBooks && (
        <>
          <h2 className="text-2xl font-semibold mt-12 mb-6 text-gray-800">
            📚 My Issued Books
          </h2>
          {issuedBooks.length === 0 ? (
            <p className="text-gray-600">You have no issued books.</p>
          ) : (
            <div className="space-y-4">
              {issuedBooks.map((book) => {
                const fine = calculateFine(book.returnDate);
                return (
                  <div
                    key={book.id}
                    className="bg-gray-100 p-4 rounded-md shadow flex flex-col sm:flex-row justify-between items-center"
                  >
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">
                        {book.title}
                      </h3>
                      <p>Issue Date: {book.issueDate}</p>
                      <p>Return Date: {book.returnDate}</p>
                    </div>
                    <div className="mt-2 sm:mt-0 text-right">
                      <p>
                        Fine:{" "}
                        <span
                          className={
                            fine > 0 ? "text-red-600" : "text-green-600"
                          }
                        >
                          ₹{fine}
                        </span>
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default BooksManager;
